/**
 * 合并
 * @param {Array ...} 多个数组传入
 * @returns {Array} 新数组
 */
declare function mergeArray(): any[];
/**
 * 合并去重
 * @param {Array ...} 多个数组传入，仅限单层数组
 * @returns {Array} 新数组
 */
declare function mergeRemoveSame(): any[];
/**
 * 合并取不重复的值
 * @param {Array ...} 多个数组传入，仅限单层数组
 * @returns {Array} 新数组
 */
declare function mergeGetOnly(): any[];
